#include<bits/stdc++.h>using namespace std;typedef long long ll;vector<int>factorize(int x){    vector<int>res;    for (int i = 1; i*i<=x; i++)    {        if(x%i==0){            res.push_back(i);            if(x/i!=i)res.push_back(x/i);        }    }    return res;}const int maxn=1e5;int last_pos[maxn+1];void solve(){    int n,x,y;    cin>>n;    for (int i = 0; i < n; i++)    {        cin>>x>>y;        vector<int>divisors=factorize(x);        int cnt=0,sz=divisors.size();        if(y>0){            for(int p:divisors)                if(last_pos[p]>=(i-y))cnt++;          }        cout<<sz-cnt<<'\n';        for(int p:divisors)last_pos[p]=i;                }  } int main(){ios::sync_with_stdio(0);cin.tie(0);for (int i = 0; i<=maxn;i++)last_pos[i]=-1;solve();     return 0;}