#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}bool check(ll k,ll h,vector<ll>&arr){    vector<ll>temp;    for (ll i = 0; i<k; i++)temp.push_back(arr[i]);    sort(begin(temp),end(temp));     ll sum=0,i=k-1;    while (i>=0)    {        sum+=temp[i];        i-=2;        if(sum>h)return false;    }    return true;}void solve(){    ll n,h;    cin>>n>>h;    vector<ll>arr(n);    f(n,arr);    ll l=1,r=n,ans=0;    while (l<=r)    {        ll mid=l+(r-l)/2;        if(check(mid,h,arr)){            ans=mid;            l=mid+1;        }        else r=mid-1;    }    cout<<ans<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);solve();     return 0;}