#include<bits/stdc++.h>using namespace std;typedef long long ll;string req="wow";void solve(){      string s;    cin>>s;    ll n=s.length();    string t="";    for (ll i = 0; i <n;)    {       ll j=i+1;       if(s[i]=='v'){        while (j<n && s[j]==s[i] && s[j]=='v')       {        j++;       }       if(j-i>1)t+=string(j-i-1,'w');        }        else t+=s[i];        i=j;    }     ll len=t.length();    ll dp[3];    // dp[0] => no. of ways to form w    // dp[1] => no. of ways to form wo    //  dp[2] => no. of ways to form wow    for (ll i = 0; i < 3; i++)dp[i]=0;        for (ll i = 0; i < len; i++)    {        if(t[i]=='w'){            dp[0]++;            dp[2]+=dp[1];        }        else dp[1]+=dp[0];    }    cout<<dp[2]<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);solve();    return 0;}