#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    ll n,m,k;    cin>>n>>m>>k;    vector<ll>h(n);    f(n,h);    ll bag=m;    for (ll i = 0; i < n-1; i++)    {        ll need=max(0ll,h[i+1]-k);        bag+=(h[i]-need);        if(bag<0){            cout<<"NO\n";            return;        }            }     cout<<"YES\n";    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}