#include<bits/stdc++.h>using namespace std;typedef long long ll;ll compute(ll x,ll y){    ll i=1;    ll temp=x*y;    ll sum=0;    while (y*i<=temp)    {        sum+=((y*i)%10);        i++;    }    return sum;}void solve(){    ll n,m;    cin>>n>>m;    if(m>n){        cout<<0<<'\n';        return;    }    int cycle_len;    if(m%2==0)cycle_len=5;    else if ((m%10)!=5)cycle_len=10;    else cycle_len=2;    ll sum=0;    if(m*cycle_len>=n){        ll i=1;        while (i*m<=n)        {            sum+=((i*m)%10);            i++;        }    }    else{        ll cycle_sum=compute(cycle_len,m);        ll k=n/(cycle_len*m);        sum=k*cycle_sum;         ll val=m*(k*cycle_len+1);        while (val<=n)        {            sum+=(val%10);            val+=m;        }    }    cout<<sum<<'\n'; } int main(){ll t;cin>>t;while (t--){    solve();}      return 0;}