#include<bits/stdc++.h>using namespace std;void solve(){    int n;    cin>>n;    vector<pair<int,int>>arr(n);    for (int i = 0; i < n; i++)        cin>>arr[i].first>>arr[i].second;    if(n==1){        cout<<0<<'\n';        return;    }    sort(begin(arr),end(arr));    int prev_l=arr[0].first,prev_r=arr[0].second,len=0;    for (int i = 1; i < n; i++)    {        int curr_l=arr[i].first,curr_r=arr[i].second;        if(curr_l<=prev_r)prev_l=curr_l;        else{            len=arr[n-1].first-prev_r;            break;        }        prev_r=min(curr_r,prev_r);    }    cout<<len<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);int t;cin>>t;while (t--){    solve();}     return 0;}