#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    ll n;    cin>>n;    int k=n*n;    int f=1;    vector<vector<int>>grid(n,vector<int>(n));    for (int i = 0; i < n; i++)    {        f^=1;        for (int j = 0; j < n; j++)        {            grid[j][i]=k;            if(j!=n-1){            if(f) k++;            else k--;            }        }        k-=n;    }     for (int i = 0; i < n; i++)    {        for (int j = 0; j < n; j++)        {           cout<<grid[i][j]<<" ";                   }        cout<<'\n';    }        } int main(){solve();     return 0;}