#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    ll n;    cin>>n;    vector<ll>arr1(n),arr2(n);    f(n,arr1),f(n,arr2);    vector<ll>pos(n+1);    for (ll i = 0; i < n; i++) pos[arr2[i]]=i;    vector<ll>c(n);    for (ll i = 0; i < n; i++)c[i]=pos[arr1[i]];        ll cnt=0,mx=-1;    for (ll i = 0; i < n; i++)    {       if(c[i]>mx)mx=c[i];       else cnt++;    }    cout<<cnt<<'\n';          } int main(){solve();     return 0;}