#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    ll n,r;    cin>>n>>r;    vector<ll>arr(n);    f(n,arr);    sort(begin(arr),end(arr));    ll op=0,carry=0;    for (ll i = n-1; i>=0;)    {                if(arr[i]-carry<=0)break;        ll j=i;        while (arr[j]==arr[i])        {            j--;        }        i=j;        op++;        carry+=r;    }    cout<<op<<"\n";    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}