#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}ll power(ll x,ll y){    if(y==0)return 1;     ll half=power(x,y/2);    if(y&1)return half*half*x;    return half*half;}void solve(){    ll n;    cin>>n;    while (true)    {        ll curr=n;        bool f=true;        while (curr>0)        {            if(f && curr%3==2)f=0;            curr/=3;        }        if(f)break;        n++;    }    cout<<n<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}