#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>pos(n+1);    for (ll i = 0; i < n; i++)    {       ll x;       cin>>x;       pos[x]=i;    }    ll minpos=n,maxpos=-1;    string ans="";    for (ll i=1; i<=n; i++)    {       minpos=min(minpos,pos[i]);       maxpos=max(maxpos,pos[i]);        if(maxpos-minpos+1==i)ans+='1';       else ans+='0';    }    cout<<ans<<'\n';        } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}