#include<bits/stdc++.h>using namespace std;typedef long long ll;void solve(){    ll n,sx,sy;    cin>>n>>sx>>sy;    vector<pair<ll,ll>>coord(n);    for (ll i = 0; i < n; i++)cin>>coord[i].first>>coord[i].second;     ll dx[]={-1,0,1,0};    ll dy[]={0,1,0,-1};        ll x=0,y=0,bestcnt=0;    ll earlier_dist=LLONG_MAX;    for (ll i = 0; i < 4; i++)    {       ll nx=sx+dx[i],ny=sy+dy[i];       if(nx<0 || ny<0)continue;       ll cnt=0;       for(auto it:coord){        ll cx=it.first,cy=it.second;        if(nx>=min(sx,cx) && nx<=max(sx,cx) &&             ny>=min(sy,cy) && ny<=max(sy,cy))cnt++;       }       if(cnt>bestcnt){        bestcnt=cnt;        x=nx,y=ny;       }    }    cout<<bestcnt<<'\n';    cout<<x<<" "<<y<<'\n';        } int main(){solve();    return 0;}