#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    ll n,s;    cin>>n>>s;    vector<ll>arr(n);    f(n,arr);    ll mx_id=0;    ll sum=0;    bool have_skipped=0;    for (ll i = 0; i < n; i++)    {        sum+=arr[i];        if(arr[i]>arr[mx_id])mx_id=i;        if(sum>s){            cout<<mx_id+1<<'\n';            return;        }     }    if(sum<=s)mx_id=-1;    cout<<mx_id+1<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}