#include<bits/stdc++.h>using namespace std;typedef long long ll;unordered_map<ll,ll>mp;void prime_factors(ll n){    for (ll i = 2; i*i<=n; i++)    {        while (n%i==0)        {            mp[i]++;            n/=i;        }    }    if(n>1)mp[n]++;    return;}ll power(ll a,ll b){    if(b==0)return 1;     ll half_power=power(a,b/2);    if(b&1)return half_power*half_power*a;    return half_power*half_power;}void solve(){    ll n;    cin>>n;    // prime factorization    prime_factors(n);    if(mp.size()>=3){        ll cnt=0,a=1,b=1,c=1;        for(auto it=mp.begin();it!=mp.end();it++){            ll u=it->first,v=it->second;            if(cnt==0)a*=(power(u,v));            else if(cnt==1)b*=power(u,v);            else c*=power(u,v);            cnt++;        }        cout<<"YES"<<'\n';        cout<<a<<" "<<b<<" "<<c<<'\n';    }    else if (mp.size()==1)    {        ll u=mp.begin()->first,v=mp.begin()->second;        if(v<6)cout<<"NO"<<'\n';        else{            ll a=u,b=u*u,c=power(u,v-3);            cout<<"YES"<<'\n';            cout<<a<<" "<<b<<" "<<c<<'\n';            }    }else if (mp.size() == 2) {    auto it = mp.begin();    ll p = it->first, x = it->second;    it++;    ll q = it->first, y = it->second;     // Case 1: p^x * q^y, both exponents >= 2    if (x >= 2 && y >= 2) {        ll a = p;        ll b = q;        ll c = power(p, x - 1) * power(q, y - 1);        cout << "YES\n";        cout << a << " " << b << " " << c << "\n";    }    // Case 2: one exponent >= 3    else if (x >= 3) {        ll a = p;        ll b = power(p, 2);        ll c = power(p, x - 3) * power(q, y);        cout << "YES\n";        cout << a << " " << b << " " << c << "\n";    }    else if (y >= 3) {        ll a = q;        ll b = power(q, 2);        ll c = power(q, y - 3) * power(p, x);        cout << "YES\n";        cout << a << " " << b << " " << c << "\n";    }    else {        cout << "NO\n";    }}   } int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    mp.clear();    solve();}     return 0;}