#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,g,b;    cin>>n>>g>>b;    if(n<=g)cout<<n<<'\n';    else{        ll temp=(n+1)/2;        ll block=g+b;        ll days=(temp/g)*block;        if(temp%g==0)days-=b;        else days+=(temp%g);        cout<<max(n,days)<<'\n';    }} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}