#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    ll n,m;    cin>>n>>m;    vector<pair<char,ll>>vec(n);    for (ll i = 0; i < n; i++){        char c;        cin>>c;        vec[i]={c,0};    }    vector<ll>idx(m);    f(m,idx);        for (ll i = 0; i < m; i++)    {       vec[0].second++;       if(idx[i]!=n)vec[idx[i]].second--;    }        for (ll i = 1; i < n; i++)        vec[i].second+=vec[i-1].second;    vector<ll>freq(26,0);    for (ll i = 0; i < n; i++)    {       freq[vec[i].first-'a']+=vec[i].second+1;    }        for (ll i = 0; i < 26; i++)    {        cout<<freq[i]<<" ";    }    cout<<'\n';                     } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}