#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}unordered_set<ll>used;void solve(){    ll n,k;    cin>>n>>k;    vector<ll>arr(n);    f(n,arr);    bool ok=true;    for (ll i = 0; i < n && ok; i++)    {       ll x=arr[i],p=0;        while (x>0)       {         ll r=x%k;         if(r>1){            ok=false;            break;         }          if(r==1){            if(used.count(p)){                ok=false;                break;            }            used.insert(p);         }         x/=k;         p++;       }           }    cout<<(ok?"YES":"NO")<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    used.clear();    solve();}    return 0;}