#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    string s;    cin>>s;    int mini=27;    vector<ll>idx;    for (ll i = 0; i < n; i++)mini=min(mini,s[i]-'a');    for (ll i = 0; i < n; i++)        if((s[i]-'a')==mini)idx.push_back(i);        vector<pair<string,ll>>ans;    for (ll i=0;i<idx.size();i++)    {        string temp=s.substr(idx[i]);        string t2=s.substr(0,idx[i]);        if((n+idx[i]+1)&1)temp+=t2;        else{            reverse(begin(t2),end(t2));            temp+=t2;        }        ans.push_back({temp,idx[i]});    }    sort(begin(ans),end(ans));    cout<<ans[0].first<<'\n';    cout<<ans[0].second+1<<'\n';        }Â int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}    return 0;}