#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++){        ll b;        cin>>b;        a[i]=b-i;    }}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    unordered_map<ll,ll>mp;    ll ans=0;    for (ll i = 0; i < n; i++){        mp[arr[i]]+=(arr[i]+i);        ans=max(ans,mp[arr[i]]);    }    cout<<ans<<'\n';        } int main(){ios::sync_with_stdio(0);cin.tie(0);     solve();      return 0;}