#include<bits/stdc++.h>using namespace std;typedef long long ll;void solve(){    ll n;    string s;    cin>>n>>s;    ll l=0,r=0;    for(char c:s){        if(c=='(')l++;    }    if(l*2!=n && n&1){        cout<<-1<<'\n';        return;    }    l=0;    ll ans=0;    ll moves=0;        for (ll i = 0; i < n;)    {        bool f=0;        ll j=i+1;        if(s[i]=='(')l++;        else r++;        if(r>l)f=1;        while (l!=r && j<n)        {            if(s[j]=='(')l++;            else r++;            j++;            if(r>l)f=1;        }        if(l!=r){            cout<<-1<<'\n';            return;        }       if(f)ans+=j-i;        i=j;    }    cout<<ans<<'\n'; } int main(){solve();     return 0;}