#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>a(n),b(n);    f(n,a),f(n,b);    for (ll i = 0; i < n; i++)        a[i]-=b[i];        sort(begin(a),end(a));    ll ans=0;    for (ll i = 0; i < n; i++)    {        if(a[i]<=0)continue;        ll l=0,r=i,ans2=0;        while (l<=r)        {            ll mid=l+(r-l)/2;            if((a[mid]+a[i])>0){                ans2=mid;                r=mid-1;            }            else l=mid+1;        }        ans+=(i-ans2);     }    cout<<ans<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);    solve();      return 0;}