#include<bits/stdc++.h>using namespace std;typedef long long ll;void solve(){    ll n,k;    cin>>n>>k;    vector<ll>val(n,0);    val[1]=1;    for (ll i = 2; i<n; i++)val[i]=i+val[i-1];    ll start=1,end=n-1;    string ans(n,'a');    while (start<=end)    {        ll mid=start+(end-start)/2;        if(val[mid]==k){            ans[n-mid-1]='b',ans[n-mid]='b';            cout<<ans<<'\n';            return;        }        if(val[mid]<k)start=mid+1;        else end=mid-1;    }    ans[n-start-1]='b';    k-=val[end];    ans[n-k]='b';    cout<<ans<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}