#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void mark_peaks(vector<ll>& arr,vector<ll>& peak) {    ll n = arr.size();    for (ll i = 1; i<n-1; i++) {        if (arr[i] > arr[i - 1] && arr[i] > arr[i + 1])            peak[i] = 1;    }}void solve(){    ll n,k;    cin>>n>>k;    vector<ll>arr(n);    f(n,arr);     ll left_id=0;    vector<ll>peak_idx(n,0);    mark_peaks(arr,peak_idx);     ll cnt=0;    for (ll i = 1; i<k-1; i++)      if(peak_idx[i])cnt++;     ll best_cnt=cnt;    for (ll l=1; l+k-1<n; l++)      {        ll r=l+k-1;        if(peak_idx[l])cnt--;        if(r>=1 && peak_idx[r-1])cnt++;         if(cnt>best_cnt){            best_cnt=cnt;            left_id=l;        }      }              cout<<best_cnt+1<<" "<<left_id+1<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}