#include<bits/stdc++.h>using namespace std;typedef long long ll;ll n;// ll f(int prev_id,int curr_id,vector<ll>&arr,vector<vector<ll>>&dp){//     // basecase//     if(curr_id==n)return 0;//     if(prev_id!=-1 && dp[prev_id][curr_id]!=-1)return dp[prev_id][curr_id]; //     ll skip=f(prev_id,curr_id+1,arr,dp);//     ll take=0;//     if(prev_id==-1 || ((curr_id+1)%(prev_id+1)==0 && arr[curr_id]>arr[prev_id]))take=1+f(curr_id,curr_id+1,arr,dp); //     if(prev_id==-1)return max(take,skip);//     return dp[prev_id][curr_id]=max(take,skip);// } void solve(){    cin>>n;    vector<ll>arr(n);    for (ll i = 0; i < n; i++)cin>>arr[i];    vector<ll>dp(n,1);    ll ans=1;    for (ll i = 1; i < n; i++)    {        for (ll j = 1; j*j<=(i+1); j++)        {            if((i+1)%j==0){                ll p1=j-1;                if(p1<i & arr[p1]<arr[i])dp[i]=max(dp[i],1+dp[p1]);                 ll j2=(i+1)/j;                if(j2!=j){                    ll p2=j2-1;                    if(p2<i && arr[p2]<arr[i])dp[i]=max(dp[i],1+dp[p2]);                }            }        }        ans=max(ans,dp[i]);    }    cout<<ans<<'\n'; } int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}