#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    ll l=1,r=n-1;    ll prev=arr[0];    ll moves=1;    bool right_move=1;    ll left_sum=arr[0],right_sum=0;    while (l<=r)    {        ll sum=0;            while (l<=r && sum<=prev)            {                if(right_move){                  sum+=arr[r];                  right_sum+=arr[r];                   r--;                }                else{                    sum+=arr[l];                    left_sum+=arr[l];                    l++;                }            }        moves++;        prev=sum;        right_move^=1;    }    cout<<moves<<" "<<left_sum<<" "<<right_sum<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}