#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}vector<ll>get_factors(ll n){    vector<ll>arr;    for (ll i = 1; i*i<=n; i++)    {        if(n%i==0){            arr.push_back(i);            if(i!=n/i)arr.push_back(n/i);        }    }    sort(rbegin(arr),rend(arr));    return arr;}void solve(){    ll n,k;    cin>>n>>k;    if(n==1 || k==1){        cout<<n<<'\n';        return;    }    if(n<=k)cout<<1<<'\n';    else{    vector<ll>arr=get_factors(n);    for (ll i = 0; i < arr.size(); i++)    {        if(arr[i]<=k){            cout<<n/arr[i]<<'\n';            return;        }    }}        } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}