#include<bits/stdc++.h>using namespace std;typedef long long ll; void solve(){    int n;    cin>>n;    vector<string>grid(n);    for (int i = 0; i < n; i++) cin >> grid[i];    for (int i = 0; i < n; i++)    {        for (int j = 0; j < n; j++)        {            if(grid[i][j]=='1'){                if (i != n-1 && j != n-1) {                    if (grid[i+1][j] != '1' && grid[i][j+1] != '1') {                        cout << "NO\n";                        return;                    }                }            }              }    }    cout<<"YES"<<'\n';    return;            } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}