#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    string s;    cin>>s;    ll total_z=0,total_1=0;    for(char c:s){        if(c=='0')total_z++;        else total_1++;    }     ll curr_z=0,curr_1=0,ans=1000;    // ṭype 1 => 111111...0000000 and type 2 =>000000....11111111    for(char c:s){        if(c=='0'){            // for type 1            ans=min(ans,curr_z+total_1-curr_1);            curr_z++;        }        else{            // for type 2            ans=min(ans,curr_1+total_z-curr_z);            curr_1++;        }    }    cout<<ans<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}