#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    ll l=0,r=1,len=1;    vector<ll>ans;    ans.push_back(arr[l]);    bool inc=arr[l]<arr[r];    while (r<n)    {        if(inc!=arr[r]>arr[r-1])        {            ans.push_back(arr[r-1]);            len++;            inc^=1;        }        r++;    }    ans.push_back(arr[r-1]);    len++;    cout<<len<<'\n';    for(ll i:ans)cout<<i<<" ";    cout<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}