#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>a(n),b(n);    f(n,a),f(n,b);    bool f=1;    ll z=(b[0]==0);    for (ll i = 1; i < n; i++)    {        if(b[i]==0)z++;        if(a[i]<a[i-1])f=0;    }    if(f || (!f && z!=n && z!=0))cout<<"YES"<<'\n';    else cout<<"NO"<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}