#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>pos(n+1),mx(n,0);    for (ll i = 0; i < n; i++)    {        ll a;        cin>>a;        pos[a]=i;    }    ll ans=0;    for (ll i = 0; i < n; i++)    {        ll b;        cin>>b;        ll delta = pos[b]-i;        mx[(delta+n)%n]++;        ans=max(ans,mx[(delta+n)%n]);    }    cout<<ans<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);    solve();     return 0;}