#include<bits/stdc++.h>using namespace std;typedef long long ll;void solve(){    ll n,x,m;    cin>>n>>x>>m;    ll left=x,right=x;    ll left_range=0,right_range=0;    for (ll i = 0; i < m; i++)    {       ll a,b;       cin>>a>>b;       if(left<=b  && right>=a){       right=max(right,b),left=min(a,left);       }    }    cout<<(right-left+1)<<'\n';  } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}