#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,k;    cin>>n>>k;    vector<ll>arr(n),weights(k);    f(n,arr),f(k,weights);    sort(rbegin(arr),rend(arr));    sort(begin(weights),end(weights));     ll sum=accumulate(begin(arr),begin(arr)+k,0ll);    // if(k==n){    //     cout<<sum*2<<'\n';    //     return;    // }     ll p1=k;    for (ll i = 0; i < k; i++)    {        weights[i]--;        if(weights[i]==0)sum+=arr[i];        else{            p1+=(weights[i]-1);            sum+=arr[p1];            p1++;        }    }    cout<<sum<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}