#include<bits/stdc++.h>using namespace std;typedef long long ll;bool isPrime(ll x){    for (ll i = 2; i*i<=x; i++)    {        if(x%i==0)return false;    }    return true;}bool f(ll n){    return !(n&(n-1));}void solve(){    ll n;    cin>>n;    if(n==1)cout<<"FastestFinger";    else if (n&1)cout<<"Ashishgup";    else{        // if number is pure power of 2         if(f(n)){            if(n==2)cout<<"Ashishgup";            else cout<<"FastestFinger";        }        else{            if(isPrime(n/2))cout<<"FastestFinger";            else cout<<"Ashishgup";        }    }    cout<<'\n';    } int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}