#include<bits/stdc++.h>using namespace std;typedef long long ll;void solve(){    ll n,k;    cin>>n>>k;    unordered_map<ll,ll>rem;    for (ll i = 0; i < n; i++)    {        ll a;        cin>>a;        ll r=a%k;        if(r!=0)rem[r]++;    }     ll ans=0;    for(auto& [a,b]:rem){        ll val=(k-a) + (b-1)*k;        ans=max(ans,val);    }    if(rem.empty())cout<<0<<'\n';    else cout<<ans+1<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}