#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}ll n,x;void solve(){    cin>>n>>x;    vector<ll>arr(n);    f(n,arr);    sort(rbegin(arr),rend(arr));    ll ans=0,curr_min=LLONG_MAX,cnt=0;    for (ll i = 0; i < n; i++)    {        curr_min=min(curr_min,arr[i]);        cnt++;        if(curr_min*cnt>=x){            ans++;            cnt=0;        }    }    cout<<ans<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}