#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,k;    cin>>n>>k;    vector<ll>arr(n);    f(n,arr);    if(k==1 && arr[0]!=arr[n-1]){        cout<<-1<<'\n';        return;    }     ll cnt_diff=0,level_sum=0,prev_ele=-1,ans=1;     for (ll i = 0; i < n; i++)    {        if(arr[i]!=(level_sum+prev_ele)){            if(cnt_diff==k){                level_sum+=prev_ele;                cnt_diff=2;                ans++;              }            else{                cnt_diff++;            }            prev_ele=arr[i]-level_sum;        }    }    cout<<ans<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}