#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n),state(n);    f(n,arr),f(n,state);    priority_queue<ll>pq;    for (ll i = 0; i < n; i++)        if(state[i]==0)pq.push(arr[i]);        for (ll i = 0; i < n; i++)    {        if(state[i]==0){            arr[i]=pq.top();            pq.pop();        }    }        for(ll i:arr)cout<<i<<" ";    cout<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}