#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}ll seq(ll idx,bool add,vector<ll>&arr,vector<vector<ll>>&dp){     // basecase    if(idx==arr.size())return 0;    if(dp[idx][add]!=-1)return dp[idx][add];    ll operation=(add?arr[idx]:-arr[idx])+seq(idx+1,!add,arr,dp);     ll no_operation=seq(idx+1,add,arr,dp);     return dp[idx][add]=max(operation,no_operation);} void solve(){    ll n,q;    cin>>n>>q;    vector<ll>arr(n);    f(n,arr);    vector<vector<ll>>dp(n+1,vector<ll>(2,-1));    ll ans=seq(0,1,arr,dp);    cout<<ans<<'\n';    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}