#include<bits/stdc++.h>using namespace std;typedef long long ll;void solve(){    ll n;    cin>>n;    ll mx=0;    vector<pair<ll,ll>>q;    map<ll,ll>mp;    for (ll i = 0; i < n; i++)    {       ll a,b;       cin>>a>>b;       mp[a]++,mp[b]--;    }    ll mx_alive=0,mn_year=0;    ll carry=0;    for(auto it=mp.begin();it!=mp.end();it++){        ll year=it->first;        ll alive=it->second+carry;        if(alive>mx_alive){            mx_alive=alive;            mn_year=year;        }        carry=alive;    }    cout<<mn_year<<" "<<mx_alive<<'\n';    }Â int main(){solve();    return 0;}