#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<pair<ll,ll>>vec(n);    ll sum=0;    for (ll i = 0; i < n; i++)cin>>vec[i].first;    for (ll i = 0; i < n; i++){        cin>>vec[i].second;        sum+=vec[i].second;    }        sort(begin(vec),end(vec));     ll ans=sum;    for (ll i = 0; i < n; i++)    {        sum-=vec[i].second;        ans=min(ans,max(vec[i].first,sum));    }    cout<<ans<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}