#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}// SLIDING WINDOWvoid solve(){    ll n,w;    cin>>n>>w;    vector<ll>arr(n);    unordered_map<ll,vector<ll>>mp;    for (ll i = 0; i < n; i++)    {        cin>>arr[i];        mp[arr[i]].push_back(i+1);    }    sort(begin(arr),end(arr));    ll sum=0;    ll l=0,r=0;    while (r<n && l<=r)    {        sum+=arr[r];        while (sum>w)        {            sum-=arr[l];            l++;        }        if((l>r)){            cout<<-1<<'\n';            return;        }        if(2*sum>=w && sum<=w){            cout<<r-l+1<<'\n';            while (l<=r)            {                cout<<mp[arr[l]].back()<<" ";                mp[arr[l]].pop_back();                l++;            }            cout<<'\n';            return;        }        r++;    }    cout<<-1<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}