#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void prime_factors(ll n){    vector<pair<ll,ll>>temp;    for (ll i = 2; i*i<=n; i++)    {       ll cnt=0;       while (n%i==0)       {         cnt++;         n/=i;       }       if(cnt>0)temp.push_back({cnt,i});    }    if(n>1)temp.push_back({1,n});    sort(rbegin(temp),rend(temp));    cout<<temp[0].first<<'\n';    for (ll i = 1; i <temp[0].first; i++)      cout<<temp[0].second<<" ";    ll t=temp[0].second;    for (ll i = 1; i < temp.size(); i++)    {        for (ll j = 0; j < temp[i].first; j++)        {            t*=temp[i].second;        }    }    cout<<t<<'\n'; }void solve(){    ll n;    cin>>n;    prime_factors(n);        } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}