#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,m;    cin>>n>>m;    vector<ll>k(n),c(m);    f(n,k),f(m,c);     sort(rbegin(k),rend(k));    ll cost=0;    for (ll i = 0; i < n; i++)    {        if(c[i]<c[k[i]-1])cost+=c[i];                else{            while (i<n)            {                cost+=c[k[i]-1];                i++;            }        }    }    cout<<cost<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}