#include<bits/stdc++.h>using namespace std;typedef long long ll;void solve(){    ll n;    cin>>n;    string s,t;    cin>>s>>t;    ll z=0,o=0;     for (ll i = 0; i < n; i++)    {        if(s[i]==t[i]){            if(s[i]=='0')z++;            else o++;        }        else{            if(z==o){                ll tz=0,to=0;                while (i<n && s[i]!=t[i] )                {                    if(s[i]=='0')tz++;                    else to++;                    i++;                }                if(tz!=to){                    cout<<"NO"<<'\n';                    return;                }                i--;            }            else{                cout<<"NO"<<'\n';                return;            }        }    }    cout<<"YES"<<'\n';    return;    } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}