#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    ll min_ele=LLONG_MAX,idx=-1;    for (ll i = 0; i < n; i++)    {        cin>>arr[i];        if(min_ele>arr[i]){            min_ele=arr[i];            idx=i;        }    }    // we can fill with prime numbers    cout<<n-1<<'\n';    for (ll i = idx-1; i>=0; i--)    {        ll y=min_ele+abs(idx-i);        cout<<idx+1<<" "<<i+1<<" "<<min_ele<<" "<<y<<'\n';    }        for (ll i = idx+1; i < n; i++)    {        ll y=min_ele+abs(idx-i);        cout<<idx+1<<" "<<i+1<<" "<<min_ele<<" "<<y<<'\n';    }        } int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}