#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}bool check(string s){    ll n=s.length();    for (ll i = 0; i <n/2; i++)    {        if(s[i]!=s[n-i-1])return false;    }    return true;}void solve(){    ll n;    string s;    cin>>n>>s;        ll zeros=0;    for(char c:s)if(c=='0')zeros++;    bool rev=0,alice=1;    ll a=0,b=0;    while (zeros)    {        if(!rev && !check(s)){            alice^=1;            rev^=1;        }        else{            // if its a palindrome , try to maintain it            //  else try to make it a palindrome            if(check(s) && n&1 && s[n/2]=='0'){                        s[n/2]='1';                        if(alice)a++,alice^=1;                        else b++,alice^=1;                }            else{                for (ll i = 0; i<n; i++)                {                    if(s[i]=='0'){                        s[i]='1';                        if(alice)a++;                        else b++;                          alice^=1;                        break;                    }                }                            }           zeros--;        }    }    if(a>b)cout<<"BOB"<<'\n';    else if (b>a)cout<<"ALICE"<<'\n';    else cout<<"DRAW"<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}