#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}ll bin_search(ll i,ll left,ll right,ll req,vector<ll>&arr){        while (left<=right)        {            ll mid=left+(right-left)/2;            if(arr[mid]<=req)left=mid+1;            else right=mid-1;        }     return right-i;}void solve(){    ll n,l,r;    cin>>n>>l>>r;    vector<ll>arr(n);    for (ll i = 0; i < n; i++)    {        cin>>arr[i];    }    sort(begin(arr),end(arr));    // count the number of pairs having sum <=r    ll cnt=0;    for (ll i = 0; i < n-1; i++)    {        ll req=r-arr[i];        ll left=i+1,right=n-1;        cnt+=bin_search(i,left,right,req,arr);        cnt-=bin_search(i,left,right,l-arr[i]-1,arr);    }        cout<<cnt<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}