#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    vector<ll>arr(n);    f(n,arr);    ll prev=arr[0];    cout<<0<<" ";    for (ll i = 1; i < n; i++)    {       ll y=(~arr[i])&prev;       cout<<y<<" ";       prev=y^arr[i];     }    cout<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}