#include<bits/stdc++.h>using namespace std;typedef long long ll;void solve(){    string s,t;    cin>>s>>t;    for (ll i = 0; i < s.length(); i++)    {      string l=s.substr(0,i+1);      string r=s.substr(0,i);      reverse(begin(r),end(r));      l+=r;      if(l.find(t)!=-1){        cout<<"YES"<<'\n';        return;      }    }    cout<<"NO"<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}